import React, { Component } from 'react';
import  { product } from './Product.json';
console.log(product);
class InvoiceMidddle extends Component{
    constructor(){
super();
this.state = {

    product
    
     }
    }

    render(){        
        const product = this.state.product.map((product) =>{
            return(  
                <tr >
                  <td>{product.nombre} <span className="descript">{product.tipo}</span></td>
                  <td>{product.cantidad}</td>
                  <td>{product.valor}</td>
                </tr>         
            ) 
         })

      return(     
        <div className="middle">
        <table className="table ">           
        <tbody>
          <tr>        
            <th>PRODUCT</th>
            <th>UNIT</th>
            <th>PRICE</th>
          </tr>
        </tbody>
        <tbody>

        {product}

        <tr>
            <td>Total</td>
            <td></td>
      <td>{suma}</td>
            </tr>
        </tbody>
      </table>
     </div>
       
        );  
    }
}

var suma=0;
var json = { product };


json.forEach(function(elemento, indice) {
    suma += elemento["valor"];
});
console.log(suma);
export default InvoiceMidddle;

const formatter = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    minimumFractionDigits: 2
  })
  formatter.format(1000) // "$1,000.00"
  formatter.format(10) // "$10.00"
  formatter.format(123233000) // "$123,233,000.00"
